import React, { useState } from 'react';
import { BrowserRouter, Switch, Route, Link } from 'react-router-dom';
import './App.css';
import Nav from './components/Nav';
import Store from './components/Store';
import Cart from './components/Cart';
import Home from './components/Home';

function App() {

const [cartCount, setCartCount] = useState(0);
const [currentCart, setCurrentCart] = useState([]);

const addToCart = ( {name, price, quantity} ) => {
  setCurrentCart([{name: name, price: price, quantity: quantity}]);
}

  return (
    <div className="App">

      <BrowserRouter>
      <Nav cartCount = {cartCount} />
        <Switch>
          <Route path='/cart' render={(props) => <Cart {...props} cartCount = {cartCount} currentCart = {currentCart}/>} />
          <Route path='/store' render={(props) => <Store {...props} addToCart = {addToCart} cartCount = {cartCount} currentCart = {currentCart} />} />
          <Route path='/' render={(props) => <Home {...props} cartCount = {cartCount} />} />
        </Switch>
      </BrowserRouter>
    </div>
  );
}

export default App;
