import React, { useState } from 'react';
import { BrowserRouter, Switch, Route, Link } from 'react-router-dom';

const Item = ( {addToCart, name, price, image} ) => {

return (
    <div class='itemTile'>
        <img src={image} />
        <h2>{name}</h2>
        <h3>{price}</h3>
        <button onClick={addToCart(name, price, 1)}>Add</button>
    </div>
);
};

export default Item;
